import React from 'react';
import PropTypes from 'prop-types';
import cn from 'classnames';
import utilStyles from '../../styles/utils.module.scss';
import classes from './CardGrid.module.scss';

const CardGrid = ({
  cardGridTitle,
  children,
}) => {
  return (
    <div className="card-grid-container">
      {cardGridTitle &&
        <h2 className="title">Latest recipes</h2>
      }
      <div className={cn('cards-container', 'flexbox-grid-container')}>
        {React.Children.map(children, (child) => (
          <div className={cn('card-child-container', 'flexbox-grid-child')}>
            { child }
          </div>
        ))}
      </div>
    </div>
  );
}

CardGrid.propTypes = {
  cardGridTitle: PropTypes.string,
  children: PropTypes.node,
};

CardGrid.defaultProps = {
  cardGridTitle: '',
  children: null,
};

export default CardGrid;
