import React from 'react';
import PropTypes from 'prop-types';
import Link from 'next/link'
import cn from 'classnames';
import classes from './Card.module.scss';

const Card = ({
  cardTitle,
  cardBottomInfo,
  cardImage,
  cardLink,
}) => {
  const card = (
    <div className={classes.cardContainer}>
      <div className="media-container">
        <div className="image-container">
          <img src={cardImage}/>
        </div>
      </div>
      <div className="text-container">
        <div className="card-text-top">
          <h4 className={cn('title')}>{ cardTitle }</h4>
        </div>
        <div className="card-text-bottom">
          {
            cardBottomInfo.map((section) => (
              <div className="card-text-bottom-section">
                <p className="primary-text">{section.title}</p>
                <p className="secondary-text">{section.subtitle}</p>
              </div>
            ))
          }
        </div>
      </div>
    </div>
  );
  return (
    cardLink ? <Link href="/"><a>{ card }</a></Link> : card
  );
}

Card.propTypes = {
  cardTitle: PropTypes.string,
  cardBottomInfo: PropTypes.arrayOf(PropTypes.shape({
      title: PropTypes.string,
      subtitle: PropTypes.string,
    })
  ),
  cardImage: PropTypes.string,
  cardUrl: PropTypes.string,
};

Card.defaultProps = {
  cardTitle: '',
  cardBottomInfo: [],
  cardImage: '',
  cardLink: '',
};

export default Card;
